(ns omochi.edit-me  (:require    [jubot.handler :as handler]))(defn your-first-handler  "Welcome to jubot!"  [{:keys [text message-for-me?]}]  (if (and message-for-me? (= text "foo")) "bar"))(defn otokura-handler  "Welcome to jubot!"  [{:keys [text message-for-me?]}]  (if (and true (= text "モバマスで一番春らんまんな子の画像ください")) "http://125.6.169.35/idolmaster/image_sp/card/l/faa2ce907ee26f77d26dcbe274eeb3be.jpg"))(defn myping-handler  "jubot ping - reply with 'pong'"  [{:keys [text message-for-me?]}]  (if (and message-for-me? (= text "ping!"))    "あ"))